import React, { useCallback, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import * as THREE from 'three';
import { EarthModel } from './EarthModel';
import AirportMarkers from './AirportMarkers';
import FlightPaths from './FlightPaths';
import StarsBackground from './StarsBackground';

// --- Types ---
interface Airport {
  iata: string;
  city: string;
  country: string;
  lat: number;
  lng: number;
  timezone: string;
  description?: string;
}

interface Route {
  source: string;
  target: string;
  airline: string;
  flight_number: string;
  duration: string;
  distance_km: number;
  status: string;
}

interface FlightRoute {
  start: THREE.Vector3;
  end: THREE.Vector3;
  routeInfo: Route;
  index: number;
}

// --- Mock Data ---
const MOCK_AIRPORTS: Airport[] = [
  {
    iata: 'TLV',
    city: 'Tel Aviv',
    country: 'Israel',
    lat: 32.0114,
    lng: 34.8867,
    timezone: 'Asia/Jerusalem',
    description: 'Ben Gurion Airport',
  },
  {
    iata: 'JFK',
    city: 'New York',
    country: 'USA',
    lat: 40.6413,
    lng: -73.7781,
    timezone: 'America/New_York',
    description: 'John F. Kennedy International Airport',
  },
];

const MOCK_ROUTES: Route[] = [
  {
    source: 'TLV',
    target: 'JFK',
    airline: 'El Al',
    flight_number: 'LY001',
    duration: '11h',
    distance_km: 9100,
    status: 'active',
  },
];

function computeFlightRoutes(airports: Airport[], routes: Route[], earthRadius: number): FlightRoute[] {
  return routes.map((route, idx) => {
    const source = airports.find(a => a.iata === route.source);
    const target = airports.find(a => a.iata === route.target);
    if (!source || !target) return null;
    const phiStart = (90 - source.lat) * (Math.PI / 180);
    const thetaStart = (source.lng + 180) * (Math.PI / 180);
    const phiEnd = (90 - target.lat) * (Math.PI / 180);
    const thetaEnd = (target.lng + 180) * (Math.PI / 180);
    const start = new THREE.Vector3(
      -earthRadius * Math.sin(phiStart) * Math.cos(thetaStart),
      earthRadius * Math.cos(phiStart),
      earthRadius * Math.sin(phiStart) * Math.sin(thetaStart)
    );
    const end = new THREE.Vector3(
      -earthRadius * Math.sin(phiEnd) * Math.cos(thetaEnd),
      earthRadius * Math.cos(phiEnd),
      earthRadius * Math.sin(phiEnd) * Math.sin(thetaEnd)
    );
    return {
      start,
      end,
      routeInfo: route,
      index: idx,
    };
  }).filter(Boolean) as FlightRoute[];
}

const GalacticGlobeApp: React.FC = () => {
  // ...existing code...
  const earthRadius = 2;
  const airports = MOCK_AIRPORTS;
  const routes = MOCK_ROUTES;
  const flightRoutes = computeFlightRoutes(airports, routes, earthRadius);

  const handleSelectAirport = useCallback((airport: Airport) => {
    // Add any additional logic for airport selection here
  }, []);

  return (
    <Canvas style={{ height: 600, width: '100%' }}>
      <Suspense fallback={null}>
        <EarthModel />
        <AirportMarkers airports={airports} radius={earthRadius} onSelectAirport={handleSelectAirport} />
        <FlightPaths flightRoutes={flightRoutes} />
        <StarsBackground numStars={100} />
      </Suspense>
    </Canvas>
  );
};

export default GalacticGlobeApp;

import React, { useRef, useMemo } from 'react';
import * as THREE from 'three';

interface StarsBackgroundProps {
  numStars?: number;
  radius?: number;
  starSize?: number;
  opacity?: number;
}

const StarsBackground = React.memo(({ numStars = 10000, radius = 150, starSize = 0.15, opacity = 0.9 }: StarsBackgroundProps) => {
  const positions = useRef<Float32Array | null>(null);

  if (!positions.current) {
    const p = new Float32Array(numStars * 3);
    for (let i = 0; i < numStars; i++) {
      const phi = Math.random() * Math.PI * 2;
      const theta = Math.random() * Math.PI;
      const x = radius * Math.sin(theta) * Math.cos(phi);
      const y = radius * Math.sin(theta) * Math.sin(phi);
      const z = radius * Math.cos(theta);
      p[i * 3] = x;
      p[i * 3 + 1] = y;
      p[i * 3 + 2] = z;
    }
    positions.current = p;
  }

  const geometry = useMemo(() => {
    const geom = new THREE.BufferGeometry();
    geom.setAttribute('position', new THREE.BufferAttribute(positions.current!, 3));
    return geom;
  }, []);

  return (
    <primitive object={geometry}>
      <pointsMaterial color="#FFFFFF" size={starSize} opacity={opacity} />
    </primitive>
  );
});
StarsBackground.displayName = 'StarsBackground';

export default StarsBackground;

import React, { useEffect, useRef, useState, useCallback } from 'react';
// SigmaRenderer and Graph will be dynamically imported client-side
import { fetchGraphDataAsync } from '../../lib/graph/service';
import { motion, AnimatePresence } from 'framer-motion';
import { useTranslation } from 'react-i18next';
import type { Timeout } from 'node:timers';

export interface NetworkGraphProps {
  title?: string;
  description?: string;
  initialGraph?: any;
  onNodeClick?: (node: string) => void;
}

export default function NetworkGraph({ title, description, initialGraph, onNodeClick }: NetworkGraphProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const [graph, setGraph] = useState<any>(initialGraph || null);
  const [message, setMessage] = useState<string | null>(null);
  const messageTimeoutRef = useRef<any>(null);
  const { t } = useTranslation();

  // Show user message
  const showMessage = useCallback((msg: string, duration = 3000) => {
    setMessage(msg);
    if (messageTimeoutRef.current) clearTimeout(messageTimeoutRef.current);
    messageTimeoutRef.current = setTimeout(() => setMessage(null), duration);
  }, []);

  // Fetch graph if not provided
  useEffect(() => {
    let mounted = true;
    async function fetchGraph() {
      if (!graph) {
        const Graphology = (await import('graphology')).default;
        fetchGraphDataAsync().then((data) => {
      if (mounted) setGraph(data instanceof Graphology ? data : new Graphology());
        });
      }
    }
    fetchGraph();
    return () => {
      mounted = false;
      if (messageTimeoutRef.current) clearTimeout(messageTimeoutRef.current);
    };
  }, [graph]);

  // Mount Sigma renderer (client-side only)
  useEffect(() => {
    let renderer: any = null;
    let isMounted = true;
    async function mountSigma() {
      if (typeof window === 'undefined' || !containerRef.current || !graph) return;
      const SigmaRenderer = (await import('sigma')).default;
      containerRef.current.innerHTML = '';
      renderer = new SigmaRenderer(graph, containerRef.current, {
        renderLabels: true,
        labelColor: { color: '#BBBBBB' },
        defaultNodeColor: '#00FFFF',
        defaultEdgeColor: '#00FF00',
      });
      type ClickNodeEvent = { node: string };
      renderer.on('clickNode', (event: ClickNodeEvent) => {
        const { node } = event;
                showMessage(t('graph.nodeClicked', { node }));
        if (onNodeClick) onNodeClick(node);
      });
      renderer.on('doubleClickNode', (event: ClickNodeEvent) => {
        const { node } = event;
        showMessage(t('graph.nodeDoubleClicked', { node }));
      });
      renderer.refresh();
    }
    mountSigma();
    return () => {
      isMounted = false;
      if (renderer) renderer.kill();
    };
  }, [containerRef, graph, showMessage, t, onNodeClick]);

  return (
    <div className="relative w-full h-96 md:h-[500px] bg-gray-900 rounded-lg shadow-lg border border-gray-700 overflow-hidden">
      {/* Graph container */}
      <div
        ref={containerRef}
        className="w-full h-full"
        role="region"
        aria-label={description || title || "Network Graph Visualization"}
        tabIndex={0}
      ></div>

      {/* Title for accessibility and clarity */}
      {title && (
        <h3 className="absolute top-4 left-4 text-xl font-bold text-blue-400 z-10" id="network-graph-title">
          {title}
        </h3>
      )}

      {/* User message display */}
      <AnimatePresence>
        {message && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            className="absolute bottom-4 left-1/2 -translate-x-1/2 bg-blue-800 text-white text-sm px-4 py-2 rounded-full shadow-lg z-20"
            role="status"
            aria-live="polite"
          >
            {message}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

import React, { useState, useMemo, useCallback } from "react";
import {
  ComposableMap,
  Geographies,
  Geography
} from "react-simple-maps";
// Note: Sphere and Graticule are not available in this version of react-simple-maps
import { worldMapData } from "@/lib/map/worldMapData";

interface CountryFeature {
  rsmKey: string;
  type: string;
  properties: {
    NAME: string;
    ISO_A3: string;
    CONTINENT: string;
  };
  geometry: unknown;
}

interface InteractiveWorldMapProps {
  selectedCountryId?: string;
  onCountryClick?: (geo: CountryFeature) => void;
  className?: string;
}

const InteractiveWorldMap: React.FC<InteractiveWorldMapProps> = ({
  selectedCountryId,
  onCountryClick,
  className = ""
}) => {
  const [hoveredCountry, setHoveredCountry] = useState<string | null>(null);

  // Memoize the map projection configuration
  const mapConfig = useMemo(() => ({
    projection: "geoEqualEarth" as const,
    projectionConfig: {
      scale: 140,
         center: [0, 0] as [number, number]
    }
  }), []);

  // Handle country interactions
  const handleCountryEnter = useCallback((geo: CountryFeature) => {
    setHoveredCountry(geo.properties.ISO_A3);
  }, []);

  const handleCountryLeave = useCallback(() => {
    setHoveredCountry(null);
  }, []);

  const handleCountryClick = useCallback((geo: CountryFeature) => {
    onCountryClick?.(geo);
  }, [onCountryClick]);

  // Memoize country styling
  const getCountryStyle = useCallback((geo: CountryFeature) => {
    const isHovered = hoveredCountry === geo.properties.ISO_A3;
    const isSelected = selectedCountryId === geo.properties.ISO_A3;

    return {
      default: {
        fill: isSelected ? "#3b82f6" : isHovered ? "#1e40af" : "#374151",
        stroke: isSelected ? "#60a5fa" : isHovered ? "#3b82f6" : "#4b5563",
        strokeWidth: isSelected ? 2 : isHovered ? 1.5 : 0.5,
        outline: "none",
        transition: "all 0.3s ease-in-out",
        cursor: "pointer"
      },
      hover: {
        fill: "#1e40af",
        stroke: "#3b82f6", 
        strokeWidth: 1.5,
        outline: "none"
      },
      pressed: {
        fill: "#1e3a8a",
        stroke: "#60a5fa",
        strokeWidth: 2,
        outline: "none"
      }
    };
  }, [hoveredCountry, selectedCountryId]);

  return (
    <div className={`relative w-full h-full bg-slate-900 overflow-hidden ${className}`}>
      {/* Background gradient */}
      <div className="absolute inset-0 bg-gradient-radial from-slate-800 via-slate-900 to-black" />

      {/* Map container */}
      <div className="relative w-full h-full">
        <ComposableMap
          projectionConfig={mapConfig.projectionConfig}
          projection={mapConfig.projection}
          width={800}
          height={400}
          className="w-full h-full"
        >
          {/* Background sphere and grid lines removed due to compatibility issues */}

          {/* Countries */}
          <Geographies geography={worldMapData}>
            {({ geographies }: { geographies: CountryFeature[] }) =>
              geographies.map((geo: CountryFeature) => {
                const style = getCountryStyle(geo);
                const isHovered = hoveredCountry === geo.properties.ISO_A3;

                return (
                  <Geography
                    key={geo.rsmKey}
                    geography={geo}
                    onMouseEnter={() => handleCountryEnter(geo)}
                    onMouseLeave={handleCountryLeave}
                    onClick={() => handleCountryClick(geo)}
                    style={style}
                    className={`
                      transition-all duration-300 ease-in-out
                      ${isHovered ? 'drop-shadow-lg' : ''}
                    `}
                  />
                );
              })
            }
          </Geographies>
        </ComposableMap>
      </div>

      {/* Country info overlay */}
      {hoveredCountry && (
        <div className="absolute top-4 left-4 pointer-events-none">
          <div className="bg-slate-800/90 backdrop-blur-sm border border-slate-600 rounded-lg px-4 py-3 font-mono text-xs text-slate-200">
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 rounded-full bg-blue-400 animate-pulse" />
              <span className="text-blue-400 font-bold">
                {worldMapData.features.find(f => f.properties.ISO_A3 === hoveredCountry)?.properties.NAME}
              </span>
            </div>
            <div className="mt-1 text-slate-400">
              {worldMapData.features.find(f => f.properties.ISO_A3 === hoveredCountry)?.properties.CONTINENT}
            </div>
          </div>
        </div>
      )}

      {/* Legend */}
      <div className="absolute bottom-4 right-4 pointer-events-none">
        <div className="bg-slate-800/90 backdrop-blur-sm border border-slate-600 rounded-lg px-4 py-3 font-mono text-xs text-slate-200">
          <div className="text-cyan-400 font-bold mb-2">INTERACTIVE WORLD MAP</div>
          <div className="space-y-1">
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-gray-600 border border-gray-500" />
              <span>Default</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-blue-700 border border-blue-500" />
              <span>Hovered</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-blue-600 border border-blue-400" />
              <span>Selected</span>
            </div>
          </div>
        </div>
      </div>

      {/* Stats overlay */}
      <div className="absolute top-4 right-4 pointer-events-none">
        <div className="bg-slate-800/90 backdrop-blur-sm border border-slate-600 rounded-lg px-4 py-3 font-mono text-xs text-slate-200">
          <div className="text-orange-400 font-bold mb-2">MAP STATISTICS</div>
          <div className="space-y-1">
            <div>COUNTRIES: {worldMapData.features.length}</div>
            <div>PROJECTION: Equal Earth</div>
            {selectedCountryId && (
              <div className="border-t border-slate-600 pt-1 mt-2">
                <span className="text-yellow-400">SELECTED: {selectedCountryId}</span>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default InteractiveWorldMap;

import React, { useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface AppModalProps {
  isOpen: boolean;
  onClose: () => void;
  children: React.ReactNode;
  title?: string;
}

const AppModal = React.memo(({ isOpen, onClose, children, title }: AppModalProps) => {
  const modalRef = useRef<HTMLDivElement>(null);
  const prevActiveElement = useRef<HTMLElement | null>(null);

  useEffect(() => {
    if (isOpen) {
      prevActiveElement.current = document.activeElement as HTMLElement;
      const focusableElements = modalRef.current?.querySelectorAll(
        'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
      ) as NodeListOf<HTMLElement> | undefined;
      const firstElement = focusableElements ? (focusableElements[0] as HTMLElement) : null;
      if (firstElement) {
        firstElement.focus();
      }

      const handleKeyDown = (event: KeyboardEvent) => {
        if (event.key === 'Escape') {
          onClose();
        } else if (focusableElements && event.key === 'Tab' && focusableElements.length > 0) {
          const first = focusableElements[0];
          const last = focusableElements[focusableElements.length - 1];

          if (event.shiftKey) {
            if (document.activeElement === first) {
              last.focus();
              event.preventDefault();
            }
          } else {
            if (document.activeElement === last) {
              first.focus();
              event.preventDefault();
            }
          }
        }
      };
      document.addEventListener('keydown', handleKeyDown);
      return () => {
        document.removeEventListener('keydown', handleKeyDown);
        if (prevActiveElement.current) {
          prevActiveElement.current.focus();
        }
      };
    }
  }, [isOpen, onClose]);

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50 p-4"
          aria-modal="true"
          role="dialog"
        >
          <motion.div
            initial={{ y: -50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            exit={{ y: -50, opacity: 0 }}
            transition={{ duration: 0.3, ease: "easeOut" }}
            className="bg-gray-900 rounded-lg shadow-xl p-6 max-w-md w-full border border-gray-700"
            ref={modalRef}
            role="document"
          >
            <div className="flex justify-between items-center mb-4 pb-4 border-b border-gray-700">
              <h2 className="text-xl font-bold text-white" id="modal-title">{title}</h2>
              <button
                onClick={onClose}
                className="text-gray-400 hover:text-white transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500 rounded-full p-1"
                aria-label="Close modal"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="2">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            <div className="text-gray-300" aria-labelledby="modal-title">
              {children}
            </div>
          </motion.div>
        </motion.div>
